sql_mode = "STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION"
autocommit = OFF

log-error     = /var/log/mysql/error.log
plugin_dir    = /usr/lib/x86_64-linux-gnu/mysql/plugin/

pid-file      = /var/run/mysqld/mysqld.pid
datadir       = /var/lib/mysql
tmpdir        = /tmp
socket        = /var/run/mysqld/mysqld.sock
mysqlx_socket = /var/run/mysqld/mysqlx.sock
lc-messages-dir = /usr/share/mysql-8.0
lc-messages = en_US
character_sets_dir = /usr/share/mysql-8.0/charsets/

admin_tls_version = TLSv1.3,TLSv1.2
tls_version = TLSv1.3,TLSv1.2
admin_tls_ciphersuites = TLS_AES_128_GCM_SHA256:TLS_CHACHA20_POLY1305_SHA256:TLS_AES_256_GCM_SHA384
tls_ciphersuites = TLS_AES_128_GCM_SHA256:TLS_CHACHA20_POLY1305_SHA256:TLS_AES_256_GCM_SHA384
admin_ssl_cipher = ECDHE-ECDSA-CHACHA20-POLY1305:ECDHE-RSA-CHACHA20-POLY1305:ECDHE-ECDSA-AES256-GCM-SHA384:ECDHE-RSA-AES256-GCM-SHA384:ECDHE-ECDSA-AES128-GCM-SHA256:ECDHE-RSA-AES128-GCM-SHA256
ssl_cipher = ECDHE-ECDSA-CHACHA20-POLY1305:ECDHE-RSA-CHACHA20-POLY1305:ECDHE-ECDSA-AES256-GCM-SHA384:ECDHE-RSA-AES256-GCM-SHA384:ECDHE-ECDSA-AES128-GCM-SHA256:ECDHE-RSA-AES128-GCM-SHA256

binlog_format = ROW
# 4096,32768
binlog_cache_size = 1MB
log_bin = binlog
# server_id = 100
# 1,1000000,25000
binlog_transaction_dependency_history_size = 25000
# COMMIT_ORDER, WRITESET, WRITESET_SESSION
binlog_transaction_dependency_tracking = WRITESET
#slave_parallel_type = LOGICAL_CLOCK
#slave_parallel_workers = 128
#slave_preserve_commit_order = ON
replica_parallel_type = LOGICAL_CLOCK
replica_parallel_workers = 128
replica_preserve_commit_order = ON

# file 
# 
innodb_file_per_table = ON
# logfile,4MB,48MB
# (innodb_log_file_size * innodb_log_files_in_group) 512GB
innodb_log_file_size = 2GB
# logfile,2,100,2
innodb_log_files_in_group = 2
# ,10,2147483647
innodb_open_files = 8192
# undo log
innodb_undo_log_truncate = ON
innodb_max_undo_log_size = 1GB

# buffers
# buffer pool size,60%-80%
#  (innodb_buffer_pool_chunk_size * innodb_buffer_pool_instances)
innodb_buffer_pool_size = 2GB
# 128MB
innodb_buffer_pool_chunk_size = 128MB
# buffer pool instance,1,64,
innodb_buffer_pool_instances = 2
# log buffer size,1MB,4095MB,16MB
innodb_log_buffer_size = 64MB

# tune
# sync_binlog,0,1
#   binlog,MySQLsync_binlogbinlog
#   1 ,MySQLbinlog.
#   ,,
#   prepared.sync_binlog=1,.
#   0 MySQLbinlog,.
#   N,N01,sync_binlog=1,Nbinlog
sync_binlog = 1
# MySQLlog bufferlog file,flush()
#   0,1,2,1
#   0 log buffer  log file , log file  flush () .
#     ,,.
#   2 MySQLlog bufferlog file. flush () .
#     ,MySQLflush () .
#   1 MySQLlog bufferlog file,flush () .
innodb_flush_log_at_trx_commit = 1
# IO, ON/OFF
innodb_use_native_aio = ON
# innodbredo log,
# fsync, O_DSYNC, littlesync, nosync, O_DIRECT, O_DIRECT_NO_FSYNC
# unixfsync
# O_DIRECT : IOdirect_ioredolinux page cacheIOfsync()
# O_DIRECT_NO_FSYNC : O_DIRECTIOfsync(). MySQL 8.0.14,XFS and EXT4
innodb_flush_method = O_DIRECT_NO_FSYNC
# innodb iops,100,200,2**64-1
# io,iops/2
innodb_io_capacity = 2000
# innodb iops,100,2**64-1
# iometeriops
innodb_io_capacity_max = 4000
# page cleaner,100,2**64-1,1024
innodb_lru_scan_depth = 9000
# ,innodb_buffer_pool_instances
innodb_page_cleaners = 2
' > etc/mysql/mysql.conf.d/mysqld.cnf

sleep 1
if [[ ${_mysql_ver} > 8.0.29 ]]; then
    sed 's|^innodb_log_file_size =|#innodb_log_file_size =|g' -i etc/mysql/mysql.conf.d/mysqld.cnf
    sed 's|^innodb_log_files_in_group =|#innodb_log_files_in_group =|g' -i etc/mysql/mysql.conf.d/mysqld.cnf
    sed '/^innodb_file_per_table =/ainnodb_redo_log_capacity = 4GB' -i etc/mysql/mysql.conf.d/mysqld.cnf
fi

###############################################################################

chmod 0644 etc/mysql/conf.d/mysql.cnf
chmod 0644 etc/mysql/mysql.conf.d/mysqld.cnf
chown -R root:root /tmp/mysql

echo
sleep 2
tar -Jcvf /tmp/"mysql_${_mysql_ver}-1_amd64.tar.xz" *
echo
sleep 2
cd /tmp
sha256sum "mysql_${_mysql_ver}-1_amd64.tar.xz" > "mysql_${_mysql_ver}-1_amd64.tar.xz".sha256

cd /tmp
sleep 2
rm -fr "${_tmp_dir}"
rm -fr /tmp/mysql
echo
printf '\e[01;32m%s\e[m\n' '  done'
echo
exit

